import React from "react";
import { Button } from "react-bootstrap";
import StripeCheckout from "react-stripe-checkout";
import { useDispatch, userSelector } from "react-redux";
import { placeOrder } from "../action/orderAction";
import Error from "./Error";
import Loader from "./Loader";

const Checkout = ({ subTotal }) => {
  const orderState = userSelector((state) => state.placeOrderReducer);
  const { loading, error, success } = orderState;
  const dispatch = useDispatch();
  const tokenHandler = (token) => {
    dispatch(placeOrder(token, subTotal));
    console.log(token);
  };
  return;
  {
    loading && <Loader />;
  }
  {
    error && <Error error="something went wrong" />;
  }
  {
    success && <success success="order places success" />;
  }
  <StripeCheckout
    amount={subTotal * 100}
    shippingAddress
    token={tokenHandler}
    stripeKey="pk_test_51KO0aSSDRITguegaENHZOriCoNQ8zhgeACRMHoCkli7xdWBe4kbbXlIVicySw1SNGa4gpwdUASaWwGdq2P3BYCsW00474YSMZU"
    currency="INR"
  >
    <Button>Pay Now</Button>
  </StripeCheckout>;
};

export default Checkout;
