import React from 'react';
import { Container, Row, Col } from "react-bootstrap";
import {useSelector,useDispatch} from "react-redux";
import {AiFillMinusCircle,AiFillPlusCircle} from "react-icons/ai";
import { addToCart } from '../action/cartAction'; 


const CartScreen = () => {
    const cartState =useSelector(state=> state.cartReducer);
    const cartItems = cartState.cartItems;
    const dispatch = useDispatch();

  return (
    <>
        <Container>
            <Row>
                <Col md={6}>
                    <h1>My Cart</h1>
                    <Row>
                        {cartItems.map((item)=>(
                            <>
                                <Col md="7">
                                    <h5>{item.name} {item.varient}</h5>
                                    <h6>
                                        {" "}
                                        Price: {item.quantity} x {item.prices[0][item.varient]}={" "}
                                        {item.price}
                                    </h6>
                                    <h6>
                                        Quantity: 
                                        <AiFillMinusCircle className='text-danger'
                                            style={{cursor:"pointer"}}
                                            onClick={()=> {dispatch(addToCart())}}
                                        />&nbsp;
                                         {item.quantity} 
                                        &nbsp;<AiFillPlusCircle className='text-success'/>

                                    </h6>
                                </Col>
                                <Col md="5">
                                    <img alt={item.name} src={item.image} style={{width:'80px',height: '80px'}}/>
                                </Col>
                                <hr/>
                            </>
                        ))}
                    </Row>
                </Col>
                <Col md ={4}>
                    <h1>Payment Info</h1>
                </Col>
            </Row>
        </Container>
    </>
  );
};

export default CartScreen;
